/* USER CODE BEGIN 4 */
void EXTI0_IRQHandler(void)
{
    HAL_GPIO_EXTI_IRQHandler(Button0_Pin); // Clear interrupt flags

    uint32_t now = HAL_GetTick();

    // Debounce check - Added missing closing brace
    if (now - lastDebounceTime < DEBOUNCE_DELAY)
    {
        return;
    }
    lastDebounceTime = now;

    // Stop DMA safely
    __HAL_TIM_DISABLE_DMA(&htim2, TIM_DMA_CC1);
    HAL_DMA_Abort_IT(&hdma_tim2_ch1);

    // Increment waveform index - Proper if statement structure
    source_index++;
    if(source_index >= NUM_WAVEFORMS)
    {
        source_index = 0;
    }

    // Start DMA with new LUT
    HAL_DMA_Start_IT(&hdma_tim2_ch1,
                   (uint32_t)LUTs[source_index],
                   (uint32_t)&htim3.Instance->CCR3,
                   NS);

    // Re-enable DMA
    __HAL_TIM_ENABLE_DMA(&htim2, TIM_DMA_CC1);

    // UPDATE LCD
    lcd_command(CLEAR);
    lcd_command(CURSOR_HOME);
    lcd_putstring(LUT_Names[source_index]);
}
